import {
    takeEvery,
    put,
    delay,
    fork,
    call
} from 'redux-saga/effects';
import  userAPI  from '../../../apis/user';
import { loadUserFailed, loadUserSuccess } from '../../actions';
import * as types from '../../actions/types'

export function* onLoadUserStartAsync(){
    try{
        const response = yield call(userAPI)
        
        if (response.status === 200){
            yield delay(500);
            yield put(loadUserSuccess(response.data))
        } 
    }
    catch (error){
        yield put(loadUserFailed(error.response.data))
    }
};

export function* onLoaduser(){
    yield takeEvery(types.LOAD_USER_START, onLoadUserStartAsync)
};

export function* onCreateuser(){
    yield takeEvery(types.CREATE_USER_START, onLoadUserStartAsync)
};

const userSagas = [
    fork(onLoaduser)
];

export default userSagas;
