import React from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardTitle, MDBCardText, MDBCol } from 'mdb-react-ui-kit';
import { loadUserStart } from '../redux/actions';
import { connect } from 'react-redux';


export class UserInfo extends React.Component {

    componentDidMount(){
        this.props.loadUserStart();
    }

    renderUser(){
        return this.props.user.map(user => {
            return (
                <MDBCol>
                <MDBCard>
                    <MDBCardBody>
                        <MDBCardTitle>{user.name}</MDBCardTitle>
                        <MDBCardText>
                            {user.email}
                        </MDBCardText>
                        <MDBCardText>
                            {user.phone}
                        </MDBCardText>
                        <MDBBtn href="#">Back</MDBBtn>
                    </MDBCardBody>
                </MDBCard>
            </MDBCol>
            )
        })
    }

    render() {
        console.log(this.props)
        return (
            <div>

            </div>
        )
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
       user: state.users.users[ownProps.match.params.id - 1]
    }
}

export default connect(mapStateToProps,{
    loadUserStart,
}) (UserInfo)
