import {
    take,
    takeEvery,
    takeLatest,
    put,
    all,
    delay,
    fork,
    call
} from 'redux-saga/effects';
import * as types from '../actions/types'
import userSagas from "./handler/userSaga";

export default function* watcherSaga(){
    yield all([...userSagas])
}
