import React  from 'react'
import { MDBRow, MDBCol, MDBBtn } from "mdb-react-ui-kit";
import history from '../history';
import { connect } from 'react-redux';
import { createUserStart, updateUserStart } from '../redux/actions';
import {  toast } from 'react-toastify';

class AddEditUser extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            users: {
                name: "",
                email: "",
                phone: "",
                address: "",
            },
            editMode: false
        }
    }

    componentDidMount(){
        if(this.props.match.params.id){
            const user = this.props.user
            this.setState({...user})
            this.setState({ editMode: true})
        }
    }

    submitHandler = event => {
        event.preventDefault();
        const { name, email, phone, address} = this.state;
        const {id} = this.props.match.params.id
        event.target.className += " was-validated"; 
        if( name && email && phone && address){
            if(!this.state.editMode){
                this.props.createUserStart(this.state);
                toast.success("user added Successfully")
                setTimeout(() => history.push("/"), 500)
            }else{
                this.props.updateUserStart({id, ...this.state})
                this.setState({ editMode: false })
            }
        }
    };
    
    changeHandler = event => {
        this.setState({ [event.target.name]: event.target.value });
    };

    onGoBack(){
        history.push('/')
    }

    render() {
        const { name, email, phone, address} = this.state;
        return (
            <MDBRow>
                <MDBCol xl="5" lg="6" md="8" className="mx-auto">
                    <form
                        className="needs-validation"
                        onSubmit={this.submitHandler}
                        noValidate
                    >
                        <h4>
                            { this.state.editMode ? "Edit User" : "Add user"}
                        </h4>
                        <MDBRow>
                            <MDBCol md="12" className="mb-3">
                                <label htmlFor="user-name" className="grey-text">
                                    First name
                                </label>

                                <input
                                    value={name || "" }
                                    onChange={this.changeHandler}
                                    type="text"
                                    name="name"
                                    className="form-control"
                                    placeholder="First name"
                                    required
                                />

                                <div className="invalid-feedback">
                                    Please provide a valid Name
                                </div>
                                <div className="valid-feedback">Looks good!</div>
                            </MDBCol>

                            <MDBCol md="12" className="mb-3">
                                <label htmlFor="email" className="grey-text">Email</label>
                                <input
                                    value={email }
                                    name="email"
                                    onChange={this.changeHandler}
                                    type="text"
                                    id="defaultFormRegisterEmailEx2"
                                    className="form-control"
                                    placeholder="Email"
                                    required
                                />
                                <div className="valid-feedback">Looks good!</div>
                            </MDBCol>

                            <MDBCol md="12" className="mb-3">
                                <label htmlFor="user-phone" className="grey-text">
                                    Phone
                                </label>

                                <input
                                    value={phone || "" }
                                    onChange={this.changeHandler}
                                    type="number"
                                    className="form-control"
                                    name="phone"
                                    placeholder="Enter Phone"
                                    required
                                />

                                <div className="invalid-feedback">
                                    Please provide a valid Phone.
                                </div>
                                <div className="valid-feedback">Looks good!</div>
                            </MDBCol>

                            <MDBCol md="12" className="mb-3">
                                <label htmlFor="defaultFormRegisterPasswordEx4"className="grey-text" >
                                    Address
                                </label>
                                <input
                                    value={address || "" }
                                    onChange={this.changeHandler}
                                    type="text"
                                    id="defaultFormRegisterPasswordEx4"
                                    className="form-control"
                                    name="address"
                                    placeholder="Enter Address"
                                    required
                                />
                                <div className="invalid-feedback">
                                    Please provide a valid state.
                                </div>
                                <div className="valid-feedback">Looks good!</div>
                            </MDBCol>
                        </MDBRow>
                        
                        <div>
                            <MDBBtn style={{ marginRight: "10px"}} color="primary" type="submit" >
                                { this.state.editMode ? "Edit Uuser" : "Add user"}
                            </MDBBtn>

                            <MDBBtn type="button" onClick ={this.onGoBack}>
                                Go Back
                            </MDBBtn>
                        </div>
                    </form>
                </MDBCol>
            </MDBRow>
        )
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        user: state.users.users[ownProps.match.params.id - 1]
    }
}

export default connect(mapStateToProps, {
    createUserStart,
    updateUserStart
}) (AddEditUser);
