import {
    takeEvery,
    takeLatest,
    put,
    delay,
    fork,
    call
} from 'redux-saga/effects';

import { loadUserAPI, createUserAPI } from '../../../apis/user';
import { loadUserFailed, loadUserSuccess, createUserSuccess, createUserFailed } from '../../actions';
import * as types from '../../actions/types'

export function* onLoadUserStartAsync(){
    try{
        const response = yield call(loadUserAPI)
        
        if (response.status === 200){
            yield delay(500);
            yield put(loadUserSuccess(response.data))
        } 
    }
    catch (error){
        yield put(loadUserFailed(error.response.data))
    }
};

export function* onLoaduser(){
    yield takeEvery(types.LOAD_USER_START, onLoadUserStartAsync)
};

function* onCreateUserStartAsync(action){
    try{
        const response = yield call (createUserAPI, action.payload);

        if(response.status === 200){
            yield put(createUserSuccess(response.data))
        }
    }
    catch (error){
        yield put(createUserFailed(error.response.data))
    }
}

function* onCreateuser(){
    yield takeLatest(types.CREATE_USER_START, onCreateUserStartAsync)
};

function* onDeleteUserStartAsync(){
    yield
}

function* onDeleteuser(){
    while(true){
        const {} = yield take(types.DELETE_USER_START)
    }
};

const userSagas = [
    fork(onLoaduser),
    fork(onCreateuser),
    fork(onDeleteuser)
];

export default userSagas;
